import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch,
} from "react-router-dom";
import React,{useState} from "react";
import "./LoginPage.css";
import { Box, Input } from "@chakra-ui/react";
import { Button } from "@chakra-ui/react";
import { Tabs, TabList, TabPanels, Tab, TabPanel } from "@chakra-ui/react";
import { FcGoogle } from "react-icons/fc";
import { IoLogoLinkedin } from "react-icons/io";
import { Center, Flex, Text } from "@chakra-ui/layout";
import SideMenu from "../SettingPage/SideMenu";
import General from "../SettingPage/General";
import { AuthorizedApp } from "../SettingPage/Subscription";
import Subscription from "../SettingPage/Subscription";
import Bascis from "../SettingPage/Basics";
import ReminderForm from "../SettingPage/ReminderForm";
import EmailNotification from "../SettingPage/EmailNotification";
import ProjectPreference from "../SettingPage/ProjectPreference";
import { Advanced } from "../SettingPage/ProjectPreference";
import Head from "../../Head";
import UserDashBoard from "../UserDashBoard/UserDashBoard"
// eslint-disable-next-line
import {  FormControl,  FormLabel,  FormErrorMessage,  FormHelperText,} from "@chakra-ui/react";
const googleOAuth = require('../../utils/googleOAuth');

const Axios = require('axios');
let name

const data = {
  "password":"Amit*paswan1",
  "email" : "amit123@gmail.com",
  "name":"amit"
}
const headers = {
  'Content-Type': 'application/json',
  'Authorization': process.env.GOOGLE_CLIENT_SECRET
}
let loginSuccess = false
export default function LoginPage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false)
  const token = localStorage.getItem("token")

    // if (token) {
    //   setIsLoggedIn(true)
    // }
  // const [userDetails, setUserDetail] = useState("paswan")
  // let usrData
  // async function toggleLogin(){
  //   const email = document.getElementById('emailId').value
  //   const password = document.getElementById('passwordId').value
  //    usrData = {
  //     "password":password,
  //     "email" : email
  //   }
  //   let status
  //   let statusText


  //   const token = await Axios.post("http://localhost:8000/users/login", {usrData})
  //   localStorage.setItem("token", token)


  // }
  if(loginSuccess === false){
  return (
    <Box
      className="login-container"
      bg="#eaeaec"
      w="100%"
      h="100vh"
      p={4}
      color="black"
    >
      <Tabs variant="unstyled" align="center" className="navigation-container">
        <div>Logo</div>
        <TabList mb="40px" m="40px">
          <Tab>Login</Tab>
          <Tab>Signup</Tab>
        </TabList>

        <TabPanels>
          <TabPanel>
            <FormControl id="email">
              <FormLabel>Email address</FormLabel>
              <Input type="email" id="emailId"  />
              <FormLabel>Password</FormLabel>
              <Input type="password" id="passwordId" />
              <FormHelperText>We'll never share your email.</FormHelperText>
            </FormControl>
            <div>
              <Button
              // onClick = {toggleLogin}
                w="100%"
                my="5px"
                pr="60px"
                pl="60px"
                colorScheme="teal"
                size="lg"
                bg="black"
              >
                Login
              </Button>
            </div>
            <div>
              <Button
                leftIcon={<FcGoogle />}
                w="100%"
                my="5px"
                pr="60px"
                pl="60px"
                bg="alphaBlack"
                border="solid gray 1px"
                size="lg"
                fontWeight="light"
                borderRadius="8px"
              >
                Signin with Google
              </Button>
            </div>
            <div>
              <Button
                leftIcon={<IoLogoLinkedin />}
                w="100%"
                my="5px"
                pr="60px"
                pl="60px"
                bg="alphaBlack"
                border="solid gray 1px"
                size="lg"
                mb="20px"
                fontWeight="light"
                borderRadius="8px"
              >
                Sign in with Linkedin
              </Button>
            </div>
            <div style={{ color: "gray" }}>
              Forgot password?{" "}
              <strong style={{ color: "black" }}>click here</strong>
            </div>
          </TabPanel>

          <TabPanel>
            <div>
            <FormControl id="email">
              <FormLabel>Email address</FormLabel>
              <Input type="email" />
              <FormHelperText>We'll never share your email.</FormHelperText>
            </FormControl>
            </div>

            <Button
              w="100%"
              my="5px"
              pr="60px"
              pl="60px"
              colorScheme="white"
              size="lg"
              bg="black"
              borderRadius="8px"
            >
              Next
            </Button>
            <div style={{ margin: "5px", color: "gray" }}>or</div>
            <div>
              <Button
                leftIcon={<FcGoogle />}
                w="100%"
                my="5px"
                pr="60px"
                pl="60px"
                bg="alphaBlack"
                border="solid gray 1px"
                size="lg"
                fontWeight="light"
                borderRadius="8px"
              >
                Signin with Google
              </Button>
            </div>
            <div>
              <Button
                leftIcon={<IoLogoLinkedin />}
                w="100%"
                my="5px"
                pr="60px"
                pl="60px"
                bg="alphaBlack"
                border="solid gray 1px"
                size="lg"
                mb="20px"
                fontWeight="light"
                borderRadius="8px"
              >
                Sign in with Linkedin
              </Button>
            </div>
            <div style={{ color: "gray" }}>
              Already have an account?{" "}
              <strong style={{ color: "black" }}>Log in here</strong>
            </div>
          </TabPanel>
        </TabPanels>
      </Tabs>
    </Box>
  );
  }else {

    return (
      <UserDashBoard />
    );

  }
}
